import React, { useState } from 'react';

function SideVideo(props) {
  return (
    <div className="side-video__item" onClick={() => props.onSelectVideo(props.video)}>
      {/* Render side video content here */}
    </div>
  );
}

function MainVideo({ selectedVideo }) {
  return (
    <div className="main-video__section">
      {/* Render details and comments of the selected video here */}
      {selectedVideo && (
        <div>
          <h2>{selectedVideo.title}</h2>
          <p>{selectedVideo.description}</p>
          {/* Render comments and other video details here */}
        </div>
      )}
    </div>
  );
}

function App() {
  const [selectedVideo, setSelectedVideo] = useState(null);

  const handleSelectVideo = (video) => {
    setSelectedVideo(video);
  };

  // Sample video data
  const sideVideos = [
    { id: 1, title: 'Video 1', description: 'Description for Video 1' },
    { id: 2, title: 'Video 2', description: 'Description for Video 2' },
    // Add more videos as needed
  ];

  return (
    <div className="app__container">
      <div className="side-videos__section">
        {sideVideos.map((video) => (
          <SideVideo key={video.id} video={video} onSelectVideo={handleSelectVideo} />
        ))}
      </div>
      <MainVideo selectedVideo={selectedVideo} />
    </div>
  );
}

export default App;
